import pandas as pd
from scipy import stats

df = pd.read_csv("5G_data/ue-lte-network-traffic-stats.csv")

#Create a list of columns related to user equipments
ue_columns = [col for col in df.columns if 'web-rtc' in col or 'sipp' in col or 'web-server' in col]

#Calculate descriptives per UE 
descriptive_stats = {col: stats.describe(df[col]) for col in ue_columns}

#Calculate skewness and kurtosis per UE
skew_kurtosis = {
    col: {
        'skew':stats.skew(df[col]),
        'kurtosis':stats.kurtosis(df[col]),
    } 
    for col in ue_columns
}

#Calculate z-scores per UE
z_scores = {
    col: {
        'z_score':stats.zscore(df[col]),
    }
    for col in ue_columns
}

#Determine thresholds for user quality
threshold = {}
for col in ue_columns:
    # Calculate mean and std. dev.
    mean = descriptive_stats[col][2]
    std = descriptive_stats[col][3]
    # Define low quality threshold
    low_quality_threshold = mean - (3 * std)
    threshold[col] = low_quality_threshold

#Identify outliers for low user quality and overwrite NaN/0 values
for col in ue_columns:
    df.loc[(df[col] < threshold[col]) & (df[col] > 0), col] = threshold[col]

print(df)

