package main

import (
	"encoding/csv"
	"fmt"
	"os"
	"strconv"
)

func main() {

	csvFile, _ := os.Open("5G_data/data.csv")
	reader := csv.NewReader(csvFile)
	lines, _ := reader.ReadAll()

	// Create map to store data
	ueData := make(map[string][]float64)

	// Iterate over every record
	for _, line := range lines[1:] {
		ue := line[0]
		ueJitter, _ := strconv.ParseFloat(line[3], 64)
		ueData[ue] = append(ueData[ue], ueJitter)
	}

	// Return count of UEs having jitter value below 50 ms
	countUnder50 := 0 
	for _, val := range ueData {
		for _, innerVal := range val {
			if innerVal < 50 {
				countUnder50++
			}
		}
	}
	fmt.Printf("Number of UEs with jitter value below 50 ms: %d\n", countUnder50)
}

import pandas as pd

# Read in the CSV file
df = pd.read_csv("5G_data/ue-lte-network-traffic-stats.csv")

# Compute the aggregated device-level statistics
device_groupby = df.groupby(['DeviceID']).agg({
    'UE1:web-rtc':[np.mean, np.std, np.min, np.max, np.median],
    'UE2:sipp':[np.mean, np.std, np.min, np.max, np.median],
    'UE3:web-server':[np.mean, np.std, np.min, np.max, np.median]
}).reset_index()

# Rename the columns
renamed_device_stats = []
for col in device_groupby.columns:
    if col[1] == 'mean':
        renamed_device_stats.append(col[0] + ' Mean')
    elif col[1] == 'std':
        renamed_device_stats.append(col[0] + ' Std')
    elif col[1] == 'min':
        renamed_device_stats.append(col[0] + ' Min')
    elif col[1] == 'max':
        renamed_device_stats.append(col[0] + ' Max')
    elif col[1] == 'median':
        renamed_device_stats.append(col[0] + ' Median')
    else:
        renamed_device_stats.append(col[0])

# Create the table
device_stats_table = device_groupby.rename(columns=dict(zip(device_groupby.columns, renamed_device_stats)))

# Print the table
print('Device Level Statistics:\n')
print(device_stats_table)

